import React from 'react';

import bg from "../images/bg3.png";
import cat from "../images/cat.JPG";
import run from "../images/run.JPG";
import {makeStyles} from "@material-ui/core";
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import ResponsiveDrawer from "./Nav.jsx";
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles(theme => ({
    content:{
        flexGrow: 1,
        padding: theme.spacing(3),
        fontSize: 20,
        backgroundImage: `url(${bg})`,
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        backgroundAttachment: "fixed",
        height: "100%",
        align:'center',

    },
    cardGrid: {
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(8),
        [theme.breakpoints.up('xs')]: {
            marginLeft: 50,
        },
        [theme.breakpoints.up('sm')]: {
            marginLeft: 50,
        },
        [theme.breakpoints.up('lg')]: {
            marginLeft: 250,
        }
    },
    media: {
        height: 200,
    },
    card: {
        maxWidth: 345,

    }



}));




const About = () => {
    const classes = useStyles();
    return(
        <main className={classes.content}>
            <ResponsiveDrawer/>
            <Grid container className={classes.cardGrid} spacing={3} >
                <Grid item xs={12} sm={12} md={6}>
                    <Card className={classes.card}>
                        <CardMedia
                            className={classes.media}
                            image={cat}
                            title="gyoz"
                        />
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="h2">
                                Meow
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                Apart from being a junior web developer, I enjoy my leisure time with my friends,especially
                                with my special friend(cat), Gyoza. I enjoy running outdoor and hanging out with my friends.
                                I also enjoy the time staying at home with my cat.
                            </Typography>
                        </CardContent>

                    </Card>

                    <Card className={classes.card}>
                        <CardMedia
                            className={classes.media}
                            image={cat}
                            title="Contemplative Reptile"
                        />
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="h2">
                                Meow
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                Apart from being a junior web developer, I enjoy my leisure time with my friends,especially
                                with my special friend(cat), Gyoza. I enjoy running outdoor and hanging out with my friends.
                                I also enjoy the time staying at home with my cat.
                            </Typography>
                        </CardContent>

                    </Card>

                </Grid>
            </Grid>
        </main>

    );
}

export default About;
