import React from 'react';
//import './MainPage.css';
//import './About.css';
import cat from "../images/cat.JPG";
import run from "../images/run.JPG";
import {makeStyles} from "@material-ui/core";
import bg from "../images/bg.png";
const useStyles = makeStyles(theme => ({
    des:{
        fontSize: 20,
        align:'center',
        [theme.breakpoints.up('xs')]: {
            marginLeft: 50,
        },
        [theme.breakpoints.up('sm')]: {
            marginLeft: 50,
        },
        [theme.breakpoints.up('lg')]: {
            marginLeft: 250,
        },
        paragraph:{
            textAlign: 'center',
            margin: "auto",
            width: 600,
        }
    }
}));




const About = () => {
    return(
        <div className="About">
            <h1>About</h1>
            <p>
                Apart from being a junior web developer, I enjoy my leisure time with my friends,especially
                with my special friend(cat), Gyoza. I enjoy running outdoor and hanging out with my friends.
                I also enjoy the time staying at home with my cat.
            </p>

            <div className="row">
                <div className="col-xl-3 col-md-6 mb-4">
                    <div className="card border-0 shadow">
                        <img src={cat} className="card-img-top"
                             alt="..."/>
                        <div className="card-body text-center">
                            <div className="card-text text-black-50">Mom of my cat, Gyoza</div>
                        </div>
                    </div>
                </div>
                <div className="col-xl-3 col-md-6 mb-4">
                    <div className="card border-0 shadow">
                        <img src={run} className="card-img-top"
                             alt="..."/>
                        <div className="card-body text-center">
                            <div className="card-text text-black-50">Half-Marathon</div>
                        </div>
                    </div>
                </div>
            </div>

        </div>
    );
}

export default About;
