import { makeStyles } from '@material-ui/core/styles';
import React from "react";
import self from "../images/self.jpg";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub } from "@fortawesome/free-brands-svg-icons";
import { faLinkedin } from "@fortawesome/free-brands-svg-icons";
//import {Link} from "react-router-dom";
import bg from "../images/bg.png";
import Avatar from '@material-ui/core/Avatar';
import Grid from '@material-ui/core/Grid';


const useStyles = makeStyles(theme => ({
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
        fontSize: 20,
        backgroundImage: `url(${bg})`,
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center center",
        backgroundSize: "cover",
        backgroundAttachment: "fixed",
        height: "100%"
    },
    bigAvatar: {
        margin: 10,
        width: 200,
        height: 200,

    },
    des:{
        fontSize: 20,
        align:'center',
        [theme.breakpoints.up('xs')]: {
            marginLeft: 50,
        },
        [theme.breakpoints.up('sm')]: {
            marginLeft: 50,
        },
        [theme.breakpoints.up('lg')]: {
            marginLeft: 250,
        },
    }
}));


export default function Home() {
    const classes = useStyles();
    return(
        <main className={classes.content}>
            <div className={classes.des}>
            <Grid container alignItems="center" justify="center">
                <Avatar alt="selfie"  src={self}  className={classes.bigAvatar} />
            </Grid>
                <div >
                <h2 className="subheading">Hello I'm</h2>
                <h1 className="mb-4">Yufei Yang</h1>
                <p className="mb-4"> I am a student from University of Toronto. My specialization is in Bioinformatics and Computational Biology.
                    I also have a Statistic Major and Computer Science Minor.</p>
                </div>
            </div>

            <div id="footer">
                <ul>
                    <li><a href="https://github.com/faye-yang"><FontAwesomeIcon icon={faGithub} color="Grey" size="lg" /> </a></li>
                    <li><a href="https://www.linkedin.com/in/yufei-yang-1a420a123/"><FontAwesomeIcon icon={faLinkedin} color="Grey" size="lg"/> </a></li>
                </ul>
            </div>
        </main>
    )

}
