import React from 'react';

import bg from "../images/bg3.png";
import cat from "../images/cat.JPG";
import run from "../images/run.JPG";
import {makeStyles} from "@material-ui/core";
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import ResponsiveDrawer from "./Nav.jsx";
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles(theme => ({
    content:{
        flexGrow: 1,
        padding: theme.spacing(3),
        fontSize: 20,
        backgroundImage: `url(${bg})`,
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        backgroundAttachment: "fixed",
        height: "100%",

    },
    cardGrid: {
        align:'center',
        paddingTop: theme.spacing(4),

    },
    media: {
        height: 180,
        maxWidth: 300,
    },
    card: {
        maxWidth: 300,
    },
    con:{
        [theme.breakpoints.up('xs')]: {
            marginLeft: 5,
        },
        [theme.breakpoints.up('sm')]: {
            marginLeft: 250,
        },
        [theme.breakpoints.up('lg')]: {
            marginLeft: 250,
        }
    }
}));


const About = () => {
    const classes = useStyles();
    return(
        <main className={classes.content}>
            <ResponsiveDrawer/>
            <div className={classes.con} >
            <Grid container className={classes.cardGrid} spacing={5} >
                <Grid item xs={10} sm={12} md={6}>
                    <Card className={classes.card}>
                        <CardMedia
                            className={classes.media}
                            image={cat}
                            title="gyoza"
                        />
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="h2">
                                Meow
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                Playing with my lovely cat, Gyoza.
                            </Typography>
                        </CardContent>

                    </Card>
                </Grid>
                <Grid item xs={10} sm={12} md={6} lg={8}>
                    <Card className={classes.card}>
                        <CardMedia
                            className={classes.media}
                            image={run}
                            title="card2"
                        />
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="h2">
                                Half Marathon
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                Scotiabank water front half Marathon.
                            </Typography>
                        </CardContent>

                    </Card>
                </Grid>
            </Grid>
            </div>
        </main>

    );
}

export default About;
